const cloudinary = require('cloudinary').v2;
const dotenv = require('dotenv');
const path = require('path');

// Load env từ thư mục gốc backend
dotenv.config({ path: path.join(__dirname, '../.env') });

// Cấu hình cloudinary với thông tin từ env
cloudinary.config({
    cloud_name: process.env.CLOUDINARY_CLOUD_NAME,
    api_key: process.env.CLOUDINARY_API_KEY,
    api_secret: process.env.CLOUDINARY_API_SECRET
});

async function quickTest() {
    try {
        console.log('Cloudinary Config:', {
            cloud_name: process.env.CLOUDINARY_CLOUD_NAME,
            api_key: process.env.CLOUDINARY_API_KEY,
            api_secret: process.env.CLOUDINARY_API_SECRET ? '***[secret]***' : undefined
        });

        // Test upload một ảnh đơn giản
        const result = await cloudinary.uploader.upload('data:image/jpeg;base64,', {
            folder: 'test_upload'
        });

        console.log('✅ Upload thành công:', {
            url: result.secure_url,
            publicId: result.public_id
        });

        // Xóa ảnh test
        const deleteResult = await cloudinary.uploader.destroy(result.public_id);
        console.log('✅ Xóa ảnh thành công');

    } catch (error) {
        console.error('❌ Lỗi:', error);
    }
}

quickTest();